import { useRef } from "react";

import Card from '../ui/Card';
import classes from './NewMeetupForm.module.css';


function UpdateMeetupForm(props){

    const titleInputRef = props.title;
    const imageInputRef = props.image;
    const addressInputRef = props.address;
    const descriptionInputRef = props.description;



    function submitHandler(event) {
        event.preventDefault();

        const enteredTitle = titleInputRef.current.value;
        const enteredImage = imageInputRef.current.value;
        const enteredAddress = addressInputRef.current.value;
        const enteredDescription = descriptionInputRef.current.value;

        const meetupData = { 
            title: enteredTitle,
            image: enteredImage,
            address: enteredAddress,
            description: enteredDescription, 
        };

        props.onUpdateMeetup(meetupData);

    }

    return(
        <Card>
            <form className={classes.form} onSubmit={submitHandler}>
                <div className={classes.control}>
                    <label htmlFor='title'>Meetup Title</label>
                    <input type='text' required id='title' ref={titleInputRef}/>
                </div>
                <div className={classes.control}>
                    <label htmlFor='image'>Meetup Image</label>
                    <input type='url' required id='image' ref={imageInputRef}/>
                </div>
                <div className={classes.control}>
                    <label htmlFor='address'>Meetup Address</label>
                    <input type='text' required id='address' ref={addressInputRef}/>
                </div>
                <div className={classes.control}>
                    <label htmlFor='Description'>Description</label>
                    <textarea id='description' required rows='5' ref={descriptionInputRef}/>
                </div>
                <div className={classes.actions}>
                    <button>Update Meetup</button> 
                </div>

            </form>


        </Card>

    );
}

export default UpdateMeetupForm;
